    int x;
    scanf("%d",&x);
    while(x!=9999){
        s=(LNode*)malloc(sizeof (LNode));
        s->data=x;
        r->next=s;
        r=s;
        scanf("%d",&x);
    }
    r->next=NULL;
    return L;
