#include <string.h>

typedef char ElemType;
#define MaxSize 50
#define N 20
typedef struct {
    ElemType data[MaxSize];
    int top;
}SqStack;

//初始化栈
void InitStack(SqStack &S){
    S.top=-1;
}
//入栈
bool Push(SqStack &S,ElemType x){
    if(S.top==MaxSize-1){
        return false;
    }
    S.data[++S.top]=x;
    return true;
}

//出栈
bool Pop(SqStack &S,ElemType &x){
    if(S.top==-1){
        return false;
    }
    x=S.data[S.top--];
    return true;
}

int bracketCheck(SqStack S,char a[],int len){
    for(int i=0;i<len;i++){
        if(a[i]=='(' || a[i]=='['){
            Push(S,a[i]);//遇到左括号就压栈
        }else{
            if(S.top==-1){
                return false;//遇到右括号但栈为空表示没有左括号与它匹配，匹配失败

            }
            char topElem;
            Pop(S,topElem);//遇到右括号就出栈
            if(topElem!='(' && a[i]==')'){
                return false;//右括号匹配的不是左括号

            }
            if(topElem!='[' && a[i]==']'){
                return false;//右括号匹配的不是左括号
            }
        }
    }
    if(S.top==-1){
        return true;//匹配结束时栈空匹配成功
    }else{
        return false;//匹配结束时栈中还有元素表示匹配失败
    }
}
